import React, {useState, useEffect} from 'react'
import './App.css';
import MaterialTable from 'material-table'


function App() {



  const [data, setData] = useState([])
  const columns = [
    { title:'Episodio',field: '{$id}'},
    { title:'Nombre', field: 'name'},
    { title:'Personajes', field: 'characters.length'},
    { title:'Locación', field: 'characters'},
  ]

  useEffect(()=>{
    fetch("https://rickandmortyapi.com/api/episode")
    .then(res => res.json())
    .then(res =>{
      setData(res.results)})
  })

  return (
    <div className="App">
      <h1 align="center">Dirrectorio de universidades</h1>
      <h4>Material Table UI</h4>
      <MaterialTable
        title="Universidades Del Mundo"
        data={data}
        columns={columns}
        options={{
          search:true,
          paging:true
        }}
      />  
    </div>
  );
}

export default App;
