import React, {useEffect, useState} from 'react';

const Contador = () => {
    const [data, setData] = useState([])  

    useEffect(()=>{
        fetch("https://rickandmortyapi.com/api/character")
            .then(res => res.json())
            .then(res =>{setData(res.results)})
    })

    var stringE = JSON.stringify(data);
    var cMayus = stringE.toLowerCase();
    var letra = cMayus.match(/c/gi);

    var cuenta = 0;
    var letr = letra[0];
    var letras = [];
    var conteo = [];

    for (let i = 0; i <= letra.length; i++) {
        if ( letr == letra[i]) {
            cuenta = cuenta + 1;
        } else {
            letras.push(letr);
            conteo.push(cuenta);
            letr = letra[i];
            cuenta = 0;
        }
    }

    const total = Math.max(...conteo);
    const totalL = conteo.indexOf(total);
    const rsp = "La lertra: " + letras[totalL] + " com " + total;

    return (
        <div className="container mt-5">
            <h1>{rsp}</h1> 
      </div>
    )
};

export default Contador;
